import React, { useEffect, useState } from 'react';
import phoneIcon from './assets/phone.png';

const getNavLinkStyle = (isMobile) => ({
  fontFamily: 'Sora, sans-serif',
  fontWeight: 400,
  fontSize: isMobile ? '16px' : '18px', // Increased min font size
  lineHeight: '23px',
  letterSpacing: '-0.05em',
  color: '#1E1E1E', // Always dark
  textDecoration: 'none',
  height: '23px',
  display: 'flex',
  alignItems: 'center',
  whiteSpace: 'nowrap',
  padding: isMobile ? '0 8px' : '0 1vw', // Increased min padding
  background: 'transparent',
});

const Menu = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 700);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 700);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <nav
      className="frame-container menu-bar"
      style={{
        height: isMobile ? 'auto' : '130px',
        background: '#fff', // Ensure visible background
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: isMobile ? '0 10px' : '0 45px',
        flexWrap: 'nowrap',
        boxShadow: '0 2px 8px rgba(0,0,0,0.03)', // Subtle shadow for separation
        zIndex: 10,
      }}
    >
      {/* Left: Logo */}
      <div
        style={{
          width: isMobile ? '70px' : '100px',
          height: isMobile ? '70px' : '100px',
          borderRadius: '50%',
          background: '#FFF',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginLeft: isMobile ? '8px' : '32px',
        }}
      >
        <img
          src="/logo.png"
          alt="Booktranspo Logo"
          style={{ width: isMobile ? '60px' : '90px', height: isMobile ? '60px' : '90px', objectFit: 'contain', borderRadius: '50%', background: 'transparent' }}
        />
      </div>
      {/* Center: Navigation Links */}
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: isMobile ? '16px' : '64px',
          height: '23px',
          justifyContent: 'center',
          flexWrap: 'nowrap',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          flex: 1,
          margin: isMobile ? '0 8px' : '0 24px',
        }}
      >
        <a href="#home" style={getNavLinkStyle(isMobile)}>Home</a>
        <a href="#why-booktranspo" style={getNavLinkStyle(isMobile)}>Why Booktranspo</a>
        <a href="#delivery-partners" style={getNavLinkStyle(isMobile)}>Delivery Partners</a>
        <a href="#transport-partners" style={getNavLinkStyle(isMobile)}>Transport Partners</a>
        <a href="#join-us" style={getNavLinkStyle(isMobile)}>Join Us</a>
      </div>
      {/* Right: Phone image */}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', width: isMobile ? '120px' : '308px', height: isMobile ? '50px' : '110px', marginRight: '0px' }}>
        <img
          src={phoneIcon}
          alt="Contact Us"
          style={{ width: isMobile ? '120px' : '308px', height: isMobile ? '50px' : '110px', objectFit: 'contain', borderRadius: '100px', marginTop: isMobile ? '8px' : '20px' }}
        />
      </div>
    </nav>
  );
};

export default Menu;
